import { Link } from "react-router-dom"
import { useContext , useEffect} from 'react' 
import logolight from './imgs/logo/logolight.png'
import logodark from './imgs/logo/logodark.png'
import Toggle from './ThemeToggle'
import {ThemeContext} from "./ThemeContext"
import location_context from "./locationcontext"



 const Header = () => {

  const {theme , set_theme} = useContext(ThemeContext);
  const whereami_arr = useContext(location_context);

  useEffect(()=> { console.log(whereami) },[whereami] )

  return (
    <header className="flex justify-between items-center fixed top-0 w-full z-20 h-16 border-b-2 bg-white dark:bg-black dark:border-0">
      <Link className="w-1/4 ml-8" to="/" >
        <img src={theme === "light" ? logolight : logodark} alt="logo" className="mx-4 h-14 brightness-125 hover:brightness-100" /> 
      </Link>
      <Toggle />
      <div className="w-1/5 ">
        {whereami_arr[0] === "Reddit" ? <Link to="/reddit" > <button className={"text-red-300 ml-16 border-b-2 border-red-300 dark:text-red-600"} >Reddit</button></Link> : <Link to="/reddit" > <button className={"text-red-300 ml-16 hover:border-b-2 hover:border-red-300 dark:text-red-600"} >Reddit</button></Link>}

        {whereami_arr[0] === "Favorite" ? <Link to="/myfavorites" > <button className={"text-red-300 ml-16 border-b-2 border-red-300 dark:text-red-600"} >Favorites</button></Link> : <Link to="/myfavorites" > <button className={"text-red-300 ml-16  hover:border-b-2 border-red-300 dark:text-red-600"} >Favorites</button></Link>}
      </div>
    </header>
  )
};

export default Header
