const saveFavorites = (mealid) => {
    let foodfavs = localStorage.getItem("SavedFoods");

           if(!foodfavs) { localStorage.setItem("SavedFoods", [mealid]) }
           else{ 
        if(!localStorage.getItem("SavedFoods").split(",").includes(mealid) )
        {   localStorage.setItem("SavedFoods",[foodfavs,mealid])   }
           }
  }

  export default saveFavorites;
